@use 'functions' as *;

// 650px, 1150x, 1400px
$breakpoints-up: (
    "medium": em(650),
    "large": em(1150),
    "xlarge": em(1400)
);

// 649.98px, 1149.98px, 1439.98px
$breakpoints-down: (
    'small': em(649.98),
    'medium': em(1149.98),
    'large': em(1439.98)
);

@mixin breakpoint($size) {
    @media only screen and (min-width: map-get($breakpoints-up, $size)) {
        @content;
    }
}

@mixin breakpoint-down($size) {
    @media only screen and (max-width: map-get($breakpoints-down, $size)) {
        @content;
    }
}

